     1→import storage from "./storage";
     2→import {
     3→  ISupportedWallet,
     4→  StellarWalletsKit,
     5→  WalletNetwork,
     6→  sep43Modules,
     7→} from "@creit.tech/stellar-wallets-kit";
     8→import { Horizon } from "@stellar/stellar-sdk";
     9→import { networkPassphrase, stellarNetwork } from "../contracts/util";
    10→
    11→const kit: StellarWalletsKit = new StellarWalletsKit({
    12→  network: networkPassphrase as WalletNetwork,
    13→  modules: sep43Modules(),
    14→});
    15→
    16→export const connectWallet = async () => {
    17→  await kit.openModal({
    18→    modalTitle: "Connect to your wallet",
    19→    onWalletSelected: (option: ISupportedWallet) => {
    20→      const selectedId = option.id;
    21→      kit.setWallet(selectedId);
    22→
    23→      // Now open selected wallet's login flow by calling `getAddress` --
    24→      // Yes, it's strange that a getter has a side effect of opening a modal
    25→      void kit.getAddress().then((address) => {
    26→        // Once `getAddress` returns successfully, we know they actually
    27→        // connected the selected wallet, and we set our localStorage
    28→        if (address.address) {
    29→          storage.setItem("walletId", selectedId);
    30→          storage.setItem("walletAddress", address.address);
    31→        } else {
    32→          storage.setItem("walletId", "");
    33→          storage.setItem("walletAddress", "");
    34→        }
    35→      });
    36→      if (selectedId == "freighter" || selectedId == "hot-wallet") {
    37→        void kit.getNetwork().then((network) => {
    38→          if (network.network && network.networkPassphrase) {
    39→            storage.setItem("walletNetwork", network.network);
